import React, { useEffect} from 'react'
import {useHistory} from 'react-router-dom'
import Navbar from '../components/Navbar';
import ItemList from '../components/ItemList';

export const Home = ({setAuth}) => {
  const history = useHistory();
  const handleSignUpSuccess = () => {
    setAuth(true);
  };

  const logout = async e => {
    e.preventDefault();
    try {
      localStorage.removeItem("token");
      handleSignUpSuccess(false);
      history.push('http://localhost:3000/login');
      //toast.success("Logout successfully");
    } catch (err) {
      console.error(err.message);
    }};

  return (
    <div>
      <Navbar handleSignUpSuccess={handleSignUpSuccess}></Navbar>
      <ItemList></ItemList>
      <button onClick={e => logout(e)} className="btn btn-outline-success my-2 my-sm-0">Logout</button>
    </div>
  );
};

export default Home;
