import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import ItemFetch from '../apis/ItemFetch';
import ImageFetch from '../apis/ImageFetch';

export default function EachItem() {
    const id = useParams();
    console.log(id.itemid);
    const [items, setItems] = useState(null);

    const handleImage = async(imageid)=>{
      try{
        const response = await ImageFetch.get(`/${imageid}`);
        return(response.data.data.items[0].imagepath);
  
      }catch(err){
        console.log(err);
      }
  
    }

    useEffect(() => {
        const fetchData = async () =>{
            try{
              const response = await ItemFetch.get(`/${id.itemid}`);
              console.log(response);
              console.log(response.data.data.items);
              response.data.data.items.imageid = await handleImage(id.itemid);
              console.log(response.data.data.items.imageid);
  
              setItems(response.data.data.items);
              console.log(items);
            }catch(err){
              console.log(err);
            }
          };
          fetchData();
    }, [])
    return (
      <section>
        <div class="container">
          <div class="row">
            <div class="col-md-6">
              <img src={response.data.data.items.imageid} alt="Item Image" class="img-fluid"/>
            </div>
            <div class="col-md-6">
              <h1>{response.data.data.items.title}</h1>
              <p class="lead">{response.data.data.items.price}</p>
              <p>{response.data.data.items.description}</p>
            </div>
          </div>
        </div>
      </section>
        
    )
}
