import React,{useEffect, useContext, useState} from 'react'
import ItemFetch from '../apis/ItemFetch';
import ImageFetch from '../apis/ImageFetch';
import { ItemListContext } from '../context/ItemListContext';
import { Link } from 'react-router-dom';

export default function ItemList(props) {
  const {items, setItems} = useContext(ItemListContext);

  const handleImage = async(imageid)=>{
    try{
      const response = await ImageFetch.get(`/${imageid}`);
      console.log(JSON.stringify(response.data.data.items[0].imagepath));
      return(response.data.data.items[0].imagepath);

    }catch(err){
      console.log(err);
    }

  }
  useEffect(() => {
    const fetchData = async () =>{
      try{
        const response = await ItemFetch.get("/");
        const updatedItems = await Promise.all(response.data.data.items.map(async(item) => {
          const newImageId = await handleImage(item.imageid);
          console.log(newImageId);
          return {...item, imageid: newImageId};
        }));
      
        setItems(updatedItems);
      }catch(err){
        console.log(err);
      }
    };
    fetchData();

  },[]);
  
  return (
    <div style={{ marginTop: '20px' }}>
  <div className="container-fluid">
    <div className="row">
      {items && items.map((item) => (
        <div className="col-sm-4 item" key={item.id}>
          <Link to={`/item/${item.id}`}>
            <div className="item-img-wrapper">
              <img className="w-100 item-img" src={item.imageid} />
              <div className="item-price">{item.price}</div>
              <div className="item-title">{item.title}</div>
            </div>
            <hr className="item-divider" />
            <div className="item-availability">{item.isSold ? 'SOLD OUT' : 'AVAILABLE'}</div>
          </Link>
        </div>
      ))}
    </div>
  </div>
  <style>{`
    .item {
      padding: 0;
    }
    .item-img-wrapper {
      position: relative;
      overflow: hidden;
      height: 300px;
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
    }
    .item-img {
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      object-fit: cover;
    }
    .item-title {
      position: absolute;
      top: 50%;
      left: 0;
      transform: translateY(-50%);
      width: 100%;
      text-align: center;
      font-weight: 600;
      color: white;
      font-size: 1.2rem;
      padding: 10px;
      background-color: rgba(0,0,0,0.6);
      text-shadow: 1px 1px 1px rgba(0,0,0,0.5);
    }
    .item-price {
      position: absolute;
      top: 0;
      right: 0;
      padding: 5px 10px;
      background-color: rgba(0,0,0,0.7);
      color: white;
      font-weight: 600;
    }
    .item-divider {
      margin: 0;
      border-top: 1px solid #eee;
    }
    .item-availability {
      text-align: center;
      padding: 10px;
      font-weight: 600;
    }
  `}</style>
</div>


  );
};
