import React,{useEffect, useContext, useState} from 'react'
import ItemFetch from '../apis/ItemFetch';
import ImageFetch from '../apis/ImageFetch';
import { ItemListContext } from '../context/ItemListContext';

export default function ItemList(props) {
  const {items, setItems} = useContext(ItemListContext);

  const handleImage = async(imageid)=>{
    try{
      const response = await ImageFetch.get(`/${imageid}`);
      console.log(JSON.stringify(response.data.data.items[0].imagepath));
      return(response.data.data.items[0].imagepath);

    }catch(err){
      console.log(err);
    }

  }
  useEffect(() => {
    const fetchData = async () =>{
      try{
        const response = await ItemFetch.get("/");
        //console.log(response.data.data.items);
        //setItems(response.data.data.items);
        //console.log(items);
        const updatedItems = await Promise.all(response.data.data.items.map(async(item) => {
          // Replace the value of imageid property with your desired new value
          const newImageId = await handleImage(item.imageid);
          console.log(newImageId); // for example
          return {...item, imageid: newImageId};
        }));
        
        // set the updated items array to the state
        setItems(updatedItems);
      }catch(err){
        console.log(err);
      }
    };
    fetchData();
    //console.log(items);

  },[]);
  
  return (
    <div>
        <div className="container-fluid">
            <div className="row">
                <div className="col-sm">
                    <img className="w-100"src="https://static01.nyt.com/images/2020/05/04/arts/04marvel-item/merlin_171454440_6136d614-7b89-4b28-83a5-cf18d9c14a36-mobileMasterAt3x.jpg"/>
                    
                    <hr color="gray"/>
                    PRICE
                    <hr color="gray"/>
                    SOLD/AVAILABLE
                    <hr color="gray"/>
                
                </div>
                <div className="col-sm">
                {items.map((item) => {
                  return (

                  <div className="col-sm" key={item.id}>
                    <img className="w-100" src={item.imageid} />
                    <hr color="gray"/>
                    <div>{item.price}</div>
                    <hr color="gray"/>
                    <div>{item.isSold ? 'SOLD' : 'AVAILABLE'}</div>
                    <hr color="gray"/>
                  </div>
                );
                  })}
                </div>
            </div>
        </div>
    </div>
  );
};
