import React, { useState } from "react";
import {BrowserRouter as Router, Route, Routes} from "react-router-dom";
import Home from "./routes/Home";
import Post from "./routes/Post";
import Sell from "./routes/Sell";
import Wishlist from "./routes/Wishlist";
import { ItemListContextProvider } from "./context/ItemListContext";
import Item from "./routes/Item";
import Login from "./routes/Login";
import SignUP from "./routes/SignUP";

const App = () =>{

    const [isAuthenticated, setIsAuthenticated] = useState(false);
    return(
    <div>
        <ItemListContextProvider>
            <Router>
                    <Routes>
                        <Route exact path = "/" render={props => <Home {...props}/>}/>
                        <Route exact path = "/items/post" Component={Post}/>
                        <Route exact path = "/items/sell" Component={Sell}/>
                        <Route exact path = "/wishlist" Component={Wishlist}/>
                        <Route exact path = "/items/:itemid/" Component={Item}/>
                        <Route exact path = "/login" Component={Login}/>
                        <Route exact path = "/signup" Component={SignUP}/>
                    </Routes>
            </Router>
        </ItemListContextProvider>
    </div>
    );
};
export default App;
