import React,{useEffect, useContext, useState} from 'react'
import ItemFetch from '../apis/ItemFetch';
import ImageFetch from '../apis/ImageFetch';
import { ItemListContext } from '../context/ItemListContext';

export default function ItemList(props) {
  const {items, setItems} = useContext(ItemListContext);
  const [imageData, setImageData] = useState({});
  useEffect(() => {
    const fetchData = async () =>{
      try{
        const response = await ItemFetch.get("/");
        setItems(response.data.data.items);
        const imageRequests = response.data.data.items.map((item) =>
          ImageFetch.get(`/${item.imageid}`)
        );
        const imageResponses = await Promise.all(imageRequests);
        const imageMap = imageResponses.reduce(
          (acc, response, index) => ({
            ...acc,
            [response.data.data.items[0].imageid]: response.data.data.items[0],
          }),
          {}
        );
        setImageData(imageMap);
      }catch(err){}
    };
    fetchData();
    //console.log(items);

  },[]);
  const handleImage = async(imageid)=>{
    try{
      const response = await ImageFetch.get(`/${imageid}`);
      console.log(JSON.stringify(response.data.data.items[0].imagepath));
      return(JSON.stringify(response.data.data.items[0].imagepath));

    }catch(err){
      console.log(err);
    }

  }
  return (
    <div>
        <div className="container-fluid">
            <div className="row">
                <div className="col-sm">
                    <img className="w-100"src="https://static01.nyt.com/images/2020/05/04/arts/04marvel-item/merlin_171454440_6136d614-7b89-4b28-83a5-cf18d9c14a36-mobileMasterAt3x.jpg"/>
                    
                    <hr color="gray"/>
                    PRICE
                    <hr color="gray"/>
                    SOLD/AVAILABLE
                    <hr color="gray"/>
                
                </div>
                <div className="col-sm">
                {items.map(async (item) => {
                  return (

                  <div className="col-sm" key={item.id}>
                    <img className="w-100" src={imageData[item.imageid].imagepath}/>
                    <hr color="gray"/>
                    <div>{item.price}</div>
                    <hr color="gray"/>
                    <div>{item.isSold ? 'SOLD' : 'AVAILABLE'}</div>
                    <hr color="gray"/>
                  </div>
                );
                  })}
                </div>
            </div>
        </div>
    </div>
  );
};
